<script>
    var allowScroll = true;
    $(document).ready(function () {
        onScroll();

        $('.main-content').on("scroll", onScroll);
        
        $('.main-content').on("scroll", function() {
            var current = $('.main-menu a.menu-item.current').attr('href');
            var menu = $(current);
            menu.attr('id', 'tmp');
            document.location.hash = current;
            menu.attr('id', current.replace('#',''));
        });

        //smoothscroll
        $('.main-menu a.menu-item[href^="#"]').on('click', function (e) {
            e.preventDefault();

            if (!$(this).hasClass('current')) {
                $(document).off("scroll");
                
                $('a').each(function () {
                    $(this).removeClass('current');
                })
                $(this).addClass('current');

                $('li.parent').removeClass('open');
                $('li.parent a.current').closest('li.parent').addClass('open');
            
                var target = this.hash,
                    menu = target;
                $target = $(target);

                allowScroll = false;
                $('.main-content').stop().animate({
                    'scrollTop': $target.offset().top
                }, 100, 'swing', function () {
                    window.location.hash = target;
                    setTimeout(function() {
                        allowScroll = true;
                    }, 100);
                });
            }
        });

        if(window.location.hash && $('.main-content').scrollTop() == 0) {
            // Fragment exists
            $('.main-menu a.menu-item[href="'+window.location.hash+'"]').click();
        }
    });

    function onScroll(event){
        if (!allowScroll) {
            return;
        }

        var scrollPos = $('.main-content').scrollTop();
        $('.main-menu a.menu-item').each(function () {
            var currLink = $(this);
            var refElement = $(currLink.attr("href"));
            if (refElement.position().top <= 0 && refElement.position().top + refElement.outerHeight() > 0) {
                $('.main-menu a.menu-item').removeClass("current");
                currLink.addClass("current");
            }
            else{
                currLink.removeClass("current");
            }
        });

        $('li.parent').removeClass('open');
        $('li.parent a.current').closest('li.parent').addClass('open');
    }

    class Search {
        constructor(selector) {
            var search = this;
            this.popup = selector;

            this.load(function() {
                search.focus();
            });
        }

        keyword() {
            return this.popup.find('.search-container input').val();
        }

        load(callback) {
            var search = this;
            var url = '{{ action("App\Http\Controllers\Docs\BuilderJsController@lookup") }}';
            var keyword = this.keyword();

            if (!search.popup.find('.result-rows .loading').length) {
                search.popup.find('.result-rows').html('<span class="loading"><img src="https://upload.wikimedia.org/wikipedia/commons/7/7d/Refresh_icon.svg" /><span>searching...</span></span>');
            }

            if(search.xhr && search.xhr.readyState != 4){
                search.xhr.abort();
            }
            search.xhr = $.ajax({
                method: "GET",
                url: url,
                data: {
                    keyword: keyword
                }
            })
            .done(function( data ) {
                search.popup.find('.result-rows').html('');
                data.forEach(function(item) {
                    search.popup.find('.result-rows').append(`<a href="`+item.link+`" class="result-row">
                        <div class="result-content">
                            <label class="">`+item.title+` <span class="text-muted label-title_`+item.cat+`"> —  `+item.cat+`</span></label>
                            <p class="desc">`+item.desc+`</p>
                        </div>
                        <div class="enter-icon">
                            <svg class="SVGInline-svg SVGInline--cleaned-svg SVG-svg Icon-svg Icon--refund-svg InternalLinkIcon-svg SVG--color-svg SVG--color--gray200-svg" style="width: 12px;height: 12px;" height="16" viewBox="0 0 16 16" width="16" xmlns="http://www.w3.org/2000/svg"><path d="M10.5 5a5 5 0 0 1 0 10 1 1 0 0 1 0-2 3 3 0 0 0 0-6l-6.586-.007L6.45 9.528a1 1 0 0 1-1.414 1.414L.793 6.7a.997.997 0 0 1 0-1.414l4.243-4.243A1 1 0 0 1 6.45 2.457L3.914 4.993z" fill-rule="evenodd"></path></svg>
                        </div>
                    </a>`);

                    search.popup.find('a.result-row').on('click', function() {
                        search.hide();
                    });

                    search.popup.find('a.result-row').on('mouseenter', function() {
                        search.focus($(this));
                    });

                    if (callback != null) {
                        callback();
                    }
                });
            });
        }

        focus(item) {
            if (item == null) {
                item = this.popup.find('.result-rows .result-row').first();
            }

            if (item.length) {
                this.popup.find('.result-rows .result-row').removeClass('row-focus');
                item.addClass('row-focus');
            }
        }

        show() {
            this.popup.fadeIn();
            this.popup.find('.search-container input').focus();
        }

        hide() {
            this.popup.fadeOut();
        }

        moveUp() {
            if (!this.popup.find('.result-rows .result-row').length) {
                return;
            }

            var current = this.popup.find('.result-rows .result-row.row-focus');
            // has current item
            if (current.length) {
                // find prev item
                if(current.prev().length) {
                    current.removeClass('row-focus');
                    current.prev().addClass('row-focus');
                } else {
                    // do nothing
                }
            } else {
                this.focus();
            }
        }

        go() {
            if (this.popup.find('.result-rows .result-row.row-focus').length) {
                var href = this.popup.find('.result-rows .result-row.row-focus').attr('href');
                window.location.href = href;
                this.hide();
            }
        }

        moveDown() {
            if (!this.popup.find('.result-rows .result-row').length) {
                return;
            }

            var current = this.popup.find('.result-rows .result-row.row-focus');
            // has current item
            if (current.length) {
                // find next item
                if(current.next().length) {
                    current.removeClass('row-focus');
                    current.next().addClass('row-focus');
                } else {
                    // do nothing
                }
            } else {
                this.focus();
            }
        }
    }

    var search = new Search($('.popup'));

    $('.sidebar-search').on('click', function() {
        search.show();
    });

    $('.popup-overlay').on('click', function() {
        search.hide();
    });

    $('.search-container input').on('keyup', function(e) {
        if (e.which == 38) {    

        } else if (e.which == 40) {    

        } else if (e.which == 13) {

        } else {
            search.load(function() {
                search.focus();
            });
        }
    });

    $(document).on('keyup', function(e) {
        if (e.which == 38) {    
            search.moveUp();
        } else if (e.which == 40) {    
            search.moveDown();
        } else if (e.which == 13) {
            search.go();
        }
    });

</script>